styles:
.title {
  font-size: 1.8em;
  margin: 0px;
}
.subtitle {
  color: grey;
  margin: 0px;
  font-size: 1.4em;
}
.buttons {
  display: flex;
  justify-content: center;
  margin-top: 10px;
}
button {
  margin-left: 10px;
  margin-right: 10px;
}

.close-btn {
  position: absolute;
  right: -4px;
  top: -4px;
  transform: scale(0.6);
}
.m-top {
  margin-top: -20px;
}
.bg-white {
  background-color: white !important;
}
.txt-black {
  color: black;
}
.txt-grey {
  color: grey;
}
.full-width {
  width: 100%;
}
.box {
  max-width: 200px;
}
.has-pointer {
  cursor: pointer;
}
.flex-space-between {
  display: flex;
  align-items: center;
  justify-content: space-between;
}

h3 {
  display: inline-block;
  margin-bottom: 0;
}
.signature-container {
  display: inline-block;
  width: 100%;
}
.signature-border {
  display: inline-block;
  border: 1px #777 solid;
  margin: 20px;
  height: 200px;
}
.disabled-border {
  border: 2px #aaa dotted;
}
img {
  height: 100%;
}

.font12px {
  font-size: 12px;
}
.editor-disabled {
  filter: contrast(0.4) brightness(1.5);
}
.ql-editor {
  white-space: normal !important;
}

.tag-full-width {
  width: 100%;
}
.tag-icon {
  color: grey;
  right: 15px;
}
.tag-icon .mat-badge-content {
  background-color: #afc5b000 !important;
  right: 1px !important;
  top: 3px !important;
  color: white !important;
}
.form-tag-control-invalid .mat-form-field-label {
  color: #ff4f4f !important;
}

.full-width {
  width: 100%;
}
.is-grey {
  color: grey;
}
.r15 {
  right: 15px;
}
mat-icon span {
  background-color: #afc5b000;
  right: 1px !important;
  top: 3px !important;
  color: white;
}

span {
  margin: 2px;
}
.full-width {
  width: 100%;
  padding-bottom: 15px;
}
.flex-center {
  display: flex;
  align-items: center;
}

.import {
  display: grid;
  grid-template-columns: 100%;
  grid-gap: 2%;
  margin-bottom: 10px;
}
.assignee p {
  margin: 0;
}
.assignee h3 {
  margin: 0;
}
