import { Component } from '@angular/core';
import { FormControl } from '@angular/forms';

@Component({
  template: `
    <h1>form-signature Testing</h1>

    <form-toggle
      [formControl]="formControlEnabled"
      placeholder="Form Enabled"
    ></form-toggle>

    <form-signature
      debug="true"
      placeholder="Sign here!"
      [formControl]="testSignControl"
    >
    </form-signature>

    <h5>Value</h5>
    <pre class="">{{testSignControl.value}}</pre>
  `
})
export class TestSignatureComponent {
  formControlEnabled = new FormControl(true);

  testSignControl = new FormControl('data:image/png;base64,');

  constructor() {
    this.formControlEnabled.valueChanges.subscribe(isEnabled => {
      if (isEnabled) {
        this.testSignControl.enable();
      } else {
        this.testSignControl.disable();
      }
    });
  }
}
